-- title:  dyke march
-- author: lena schimmel
-- desc:   a game for ld50
-- script: lua

t=0
px=12
tx=12
pf=0
tb=0

levelwidth = 30
levelheight = 17

function OVR()
  map(0,0,levelwidth,levelheight,0,0,0)
end

function TIC()
 cls(11)
 map(0,17,levelwidth,levelheight,0,0,0)
 x,y,l = mouse()
 mx = math.floor(x / 8)
 my = math.floor(y / 8)
 
 sp = 6
 if isvalidblockpos(mx,my) then
  sp = 5
  if l then
		 tx = mx
			ty = my
			tb=1
			--mset(mx,my,3)
		end
 end
	spr(sp,mx*8,my*8,0)
	t=t+1
	
	for py = 0,18,1 do
	 if isblock(px,py) then
		 spr(7,px*8,(py-2)*8,0, 1, pf, 0, 1, 2)
		 break
		end	
	end
	
	if t % 20 == 0 then
	 if px < tx - 1 then
		  px = px + 1
				pf = 1
		elseif px > tx + 1 then
		  px = px - 1
				pf = 0
		elseif tb ~= 0 then
		  tb = 0
				setblock(tx,ty)
		end
	end
	
	if t % 120 == 0 then
  addwater()
		t = 0
	end
end

function isvalidblockpos(x,y)
	return isempty(x,y) and isblock(x-1,y+1) and isblock(x,y+1) and isblock(x+1,y+1)
end

function rightmostwater()
 for x = levelwidth, 0, -1 do
    for y = 0, levelheight, 1 do
      if iswater(x,y) then
        return x,y
      end
    end
  end
  return 0,15
end

function addwater()
  x,y = rightmostwater()
  
  if x == levelwidth - 1 then
    trace ("GAME OVER")
    exit()
    return
  end
  
  if isempty(x+1, y) then
   while isempty(x+1,y+1) do
    y = y + 1
   end
  	setwater(x+1, y)
   return
  end
  
  while x > 0 and isempty(x-1,y) do
   x = x - 1
  end
  
  if isempty(x,y) then
   while isempty(x+1,y+1) do
    y = y + 1
   end
   setwater(x+1, y)
  else
   y = y - 1
   while x > 0 and isempty(x-1,y) do
    x = x - 1
   end
   setwater(x,y)
  end
end

function setwater(x,y)
  if isblock(x+1,y) then
   mset(x, y+levelheight, 17)
  else
   mset(x, y+levelheight, 18)
  end
  if iswater(x-1,y) then
    mset(x-1, y+levelheight, 17)
  end
  if iswater(x,y+1) then
    mset(x, y+1+levelheight, 2)
  end
end

function iswater(x,y)
 return fget(mget(x,y+levelheight),0)
end
function isblock(x,y)
 return fget(mget(x,y),1) or fget(mget(x,y+levelheight),1)
end
function setblock(x,y)
  return mset(x,y+levelheight,3)
 end
function isempty(x,y)
 return fget(mget(x,y),2) and fget(mget(x,y+levelheight),2) and not iswater(x,y)
end

