--
-- title:  dyke march
-- author: lena schimmel
-- desc:   a game for ld50
-- script: lua
t=0 --global time
px=12 -- player x (y is not saved, but computed)
tx=12 -- target x (e.g. for construction)
pf=0 -- player flip (looking direction)
tb=0 -- to build (0 = none, 1 = dyke)
sx=0 -- scroll x in pixel
to=0 -- tool (0 = walk, 1=build, 2=collect)
sb = 30 -- scroll sensitive border
re = {5, 0, 0} -- ressources (stone, wood, score)
wt = 120 -- water tics
pt = 20 -- player tics

levelwidth = 60
levelheight = 17

function OVR()
  map(0,0,levelwidth,levelheight,-sx,0,0)

  x,y,l = mouse()
  my = y // 8

  -- show resources
  te = string.format("%2d# %2d/ %4d$",re[1],re[2],re[3])
  prints(te, 8, 8, 12)

  -- show actions
  prints("Action:", 16*6, 8, 12)
  for i=0,6 do
    spr(96 + i, 144 + 12*i, 8, 0)
    if to==i then
      spr(104, 144 + 12*i, 8, 0)
    end
  end

  ct = "" --center text
  -- draw cursor
  sp = 119
  if my > 2 then -- lower screen part
    if to == 0 then -- walk
      sp = 160 + to -- disabled
      if isempty(mx,my) then
        for ly=my+1,levelheight do
          if not isempty(mx,ly) then
            if isblock(mx, ly) then
              if ly - 1 ~= my then
                spr(122,mx*8-sx,ly*8-8,0)
              end
              sp = sp - 16 -- enable
              ct = "Walk here: 4*"
            else
              ct = "Not walkable"
            end
            break
          end
        end
      else
        ct = "Not walkable"
      end
    end
    if to == 1 and tb==0 then
      sp = 136
      if re[1] == 0 then
        ct = "No stones to build dyke"
      elseif not isvalidblockpos(mx,my) then
        ct = "Cant build dyke here"
      else
        sp = 120
        ct = "Build dyke here: 1# 7*"
        if l and re[1] > 0 then
          tx = mx
          ty = my
          tb=1
          re[1]=re[1]-1
          --mset(mx,my,3)
        end
      end
    end
    spr(sp,mx*8-sx,my*8,0)
  else
    mxc = x // 6
    if my == 1 then
      if mxc > 0 and mxc < 4 then
        ct =  re[1] .. " stones"
      end
      if mxc > 4 and mxc < 8 then
        ct =  re[2] .. " wood"
      end
      if mxc > 8 and mxc < 13 then
        ct =  re[3] .. " score"
      end
     

      for i=0,4 do
        if mxc == 24 + 2*i then
          sp = 120
          texts = {"walk", "build dyke", "collect", "cut", "play card"}
          ct = texts[i+1]
          if l then
            to = i
          end
        end
      end
    end
    spr(sp,mxc*6,my*8,0)
  end
  printc(ct, 120, 0, 12)
end

function TIC()
  -- mouse calc
  x,y,l,_,_,sxv = mouse()
  -- mouse position in grid coords
  mx = math.floor((x+sx) / 8)
  mxa = math.floor(x / 8)
  my = math.floor(y / 8)

  -- update
  t=t+1
  if t % pt == 0 then
	  if px < tx - 1 then
		  px = px + 1
				pf = 1
		elseif px > tx + 1 then
		  px = px - 1
			pf = 0
		elseif tb ~= 0 then
		  tb = 0
			setblock(tx,ty)
		end
	end
	
	if t % wt == 0 then
    addwater()
		t = 0
	end

  -- draw
  cls(11)
  map(0,17,levelwidth,levelheight,-sx,0,0)
  
  if x < sb then
    sv = sb - x
    sx = sx - sv * sv / 400
  end
  if x > (240 - sb) then
    sv = x - (240 - sb)
    sx = sx + sv * sv / 400
  end
  sx = sx + sxv * 2
  sx = math.max(sx, 0)
	sx = math.min(sx, (levelwidth * 8) - 240)
	
	for py = 0,18,1 do
    if isblock(px,py) then
      spr(7,px*8-sx,(py-2)*8,0, 1, pf, 0, 1, 2)
      break
    end	
	end
end

function isvalidblockpos(x,y)
	return isempty(x,y) and isblock(x-1,y+1) and isblock(x,y+1) and isblock(x+1,y+1)
end

function rightmostwater()
  for x = levelwidth, 0, -1 do
    for y = 0, levelheight, 1 do
      if iswater(x,y) then
        return x,y
      end
    end
  end
  return 0,15
end

function addwater()
  x,y = rightmostwater()
  
  if x == levelwidth - 1 then
    trace ("GAME OVER")
    exit()
    return
  end
  
  if isempty(x+1, y) then
    while isempty(x+1,y+1) do
      y = y + 1
    end
  	setwater(x+1, y)
   return
  end
  
  while x > 0 and isempty(x-1,y) do
    x = x - 1
  end
  
  if isempty(x,y) then
    while isempty(x+1,y+1) do
      y = y + 1
    end
    setwater(x+1, y)
  else
    y = y - 1
    while x > 0 and isempty(x-1,y) do
      x = x - 1
    end
    setwater(x,y)
  end
end

function setwater(x,y)
  if isblock(x+1,y) then
    mset(x, y+levelheight, 17)
  else
    mset(x, y+levelheight, 18)
  end
  if iswater(x-1,y) then
    mset(x-1, y+levelheight, 17)
  end
  if iswater(x,y+1) then
    mset(x, y+1+levelheight, 2)
  end
end

function iswater(x,y)
  return fget(mget(x,y+levelheight),0)
end

function isblock(x,y)
  return fget(mget(x,y),1) or fget(mget(x,y+levelheight),1)
end

function setblock(x,y)
  return mset(x,y+levelheight,3)
end

function isempty(x,y)
  return fget(mget(x,y),2) and fget(mget(x,y+levelheight),2) and not iswater(x,y)
end

--Prints text where x is the center of text.
function printc(s,x,y,c)
  local w=print(s,0,-8)
  prints(s,x-(w/2),y,c or 15, true)
end

--Prints text, replaces some symbols with sprites
function prints(s,x,y,c)
  print(s:gsub("$"," "):gsub("/"," "):gsub("#"," "):gsub("*"," "),x,y+1,c or 15, true)
  for i = 1, s:len() do
    ch = s:sub(i,i)
    sp = 0
    if(ch == "#") then -- stone
      sp = 128
    end
    if(ch == "/") then -- wood
      sp = 129
    end
    if(ch == "$") then -- score
      sp = 130
    end
    if(ch == "*") then -- time
      sp = 131
    end
    if(ch == "@") then -- card
      sp = 132
    end
    spr(sp,x+(i-1)*6,y,0)
  end
end

